import Layout from '../components/MyLayout';
import SeasonSchedules from '../data/SeasonSchedule-20192020';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

const Schedules = props => {
    console.log('Hier Props >>>>>>', props);
    return (
        <Layout>
            <h1>NHL Schedules</h1>
            <ul>
                {props.matches.map(match => (
                    <li key={match.id}>
                        {/* <Link href="/p/[id]" as={`/p/${match.id}`}> */}
                        <a>
                            <div>{match.a}</div>
                            <div>{match.h}</div>
                        </a>
                        {/* </Link> */}
                    </li>
                ))}
            </ul>
        </Layout>
    );
};
Schedules.getInitialProps = async function() {
    // const res = await import(SeasonSchedules);
    // const data = await res.json();

    // const data = await import('../data/SeasonSchedule-20192020');
    const data = SeasonSchedules;

    const searchDate = '20200212';

    // console.log('data::::' + typeof data[4].est);

    const matchesOnDate = data.filter(function(entry) {
        return entry.est.indexOf(searchDate) > -1;
    });

    // const matchesOnDate = Object.values(data).filter(entry => entry.est.indexOf(searchDate) > -1);

    console.log('matchesToday::::', matchesOnDate.length);

    return {
        matches: matchesOnDate
    };

    //Beispiel
    // {"id":2019020814,"est":"20200204 19:00:00","a":"VGK","h":"TBL"}
};
export default Schedules;
